DROP TABLE IF EXISTS `vr_course`;
CREATE TABLE `vr_course`  (
  `id` int(11) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '流水号',
  `sid` int(11) NOT NULL COMMENT '课程编号',
  `name` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '课程名称',
  `year` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '学年，“2010“ 表示 2010-2011学年',
  `term` tinyint(1) NOT NULL DEFAULT 1 COMMENT '学期，1.2',
  `content` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '课程内容',
  `credit` tinyint(4) NOT NULL DEFAULT 0 COMMENT '学分',
  `class_cycle` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '上课周期',
  `week` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '星期',
  `class_turn` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '节次',
  `address` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '上课地点',
  `unit` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '开课单位',
  `teacherid` int(11) NOT NULL COMMENT '教师id',
  `status` tinyint(1) NOT NULL DEFAULT 1 COMMENT '状态',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '课程表' ROW_FORMAT = Compact;

