  `eno` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `sid` int(11) UNSIGNED NOT NULL DEFAULT 0,
  `total_score` int(11) NOT NULL DEFAULT 455,
  `score1` int(11) NOT NULL DEFAULT 0,
  `score2` int(11) NOT NULL DEFAULT 0,
  `score3` int(11) NOT NULL DEFAULT 0,
  `score4` int(11) NOT NULL DEFAULT 0,
  `score` int(11) NOT NULL DEFAULT 0,
  `score_rule` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `word` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL,
  `ctime` datetime(0) NOT NULL,
