  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_student_id`(`sid`) USING BTREE,
  INDEX `eno`(`eno`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 346 CHARACTER SET = utf8 COLLATE = utf8_bin ROW_FORMAT = Compact;

